tinymce.PluginManager.add('editorPlugins', function (editor, url) {
  // 注册一个工具栏按钮名称

  editor.ui.registry.addButton('actions', {
    icon:'comment-add',
    text: '插入变量',
    onAction: () => {
      window.OpenVar(editor);
    }
  });

  editor.ui.registry.addButton('pay', {
    icon:'comment-add',
    text: '插入变量',
    onAction: () => {
      window.OpenPay(editor);
    }
  });

  editor.ui.registry.addButton('actionsInkind', {
    icon:'comment-add',
    text: '插入变量',
    onAction: () => {
      window.OpenInkind(editor);
    }
  });

  editor.ui.registry.addButton('actionsSku', {
    icon:'comment-add',
    text: '插入变量',
    onAction: () => {
      window.OpenSku(editor);
    }
  });

  editor.ui.registry.addButton('actionsPosition', {
    icon:'comment-add',
    text: '插入变量',
    onAction: () => {
      window.OpenPosition(editor);
    }
  });

  editor.ui.registry.addButton('actionsImg', {
    icon:'gallery',
    onAction: () => {
      window.OpenImg(editor);
    }
  });


  return {
    getMetadata() {
      return {
        // 插件名和链接会显示在“帮助”→“插件”→“已安装的插件”中
        name: 'Example plugin',// 插件名称
        url: 'http://exampleplugindocsurl.com', // 作者网址
      };
    }
  };
});
