import React, {useEffect, useState} from 'react';
import {Descriptions, Image} from 'antd';
import Empty from '@/components/Empty';
import {isArray, isObject} from '@/util/Tools';
import styles from './index.module.less';
import {useRequest} from '@/util/Request';
import {spuClassificationDetail} from '@/pages/Erp/spu/components/spuClassification/spuClassificationUrl';

const SkuDetail = (
  {
    skuDetail,
  }
) => {

  const [typeSetting, setTypeSetting] = useState([]);

  const {run: getSkuForm} = useRequest(spuClassificationDetail, {
    manual: true,
    onSuccess: (res) => {
      setTypeSetting(res && res.typeSetting && JSON.parse(res.typeSetting) || []);
    }
  });


  useEffect(() => {
    const spuClassificationId = skuDetail?.spuClass;
    if (spuClassificationId) {
      getSkuForm({data: {spuClassificationId}});
    }
  }, [skuDetail?.skuId]);

  if (!skuDetail || !skuDetail.skuId) {
    return <Empty description="请选择父件物料" />;
  }

  const spuResult = skuDetail.spuResult || {};

  return <div className={styles.detail}>
    <div className={styles.img}>
      <Image
        fallback="data:image/png;base64,"
        preview={{src: isArray(skuDetail.imgResults)[0]?.url}}
        src={isArray(skuDetail.imgResults)[0]?.thumbUrl || 'error'}
      />
    </div>
    <Descriptions
      column={1}
    >
      {

        typeSetting.filter(item => item.show).map((item, index) => {
          let children;
          switch (item.key) {
            case 'spuClass':
              children = isObject(spuResult.spuClassificationResult).name;
              break;
            case 'spu':
              children = spuResult.name;
              break;
            case 'batch':
              children = skuDetail.batch ? '一批一码' : '一物一码';
              break;
            case 'spuCoding':
              children = spuResult.coding;
              break;
            case 'unitId':
              children = isObject(skuDetail.unit).unitName || '-';
              break;
            case 'weight':
              children = `${skuDetail[item.key] || 0} kg`;
              break;
            case 'maintenancePeriod':
              children = `${skuDetail[item.key] || 0} 天`;
              break;
            case 'sku':
              children = <>(
                {
                  skuDetail.list &&
                  skuDetail.list.length > 0 &&
                  skuDetail.list[0].attributeValues ?
                    <em>{skuDetail.list.map((items) => {
                      return `${items.itemAttributeResult.attribute}: ${items.attributeValues}`;
                    }).toString()}</em> : '无'
                }
                )</>;
              break;
            case 'materialId':
              children = skuDetail.materialResult?.name;
              break;
            case 'brandIds':
              children = isArray(skuDetail.brandResults).map(item => item.brandName).join('、');
              break;
            case 'skuSize':
              children = skuDetail.skuSize && skuDetail.skuSize.split(',').join('×') || '无';
              break;
            case 'images':
            case 'fileId':
              return;
            default:
              children = skuDetail[item.key] || '-';
              break;
          }
          return <Descriptions.Item key={index} label={item.filedName}>
            {children}
          </Descriptions.Item>;
        })
      }
    </Descriptions>
  </div>;
};

export default SkuDetail;
